    public int largeur() {
        return this.codesTuiles.length;
    }

    public int largeurEnPixels() {
        return this.largeur() * 32;
    }

    public int hauteur() {
        return this.codesTuiles[0].length;
    }

    public int hauteurEnPixels() {
        return this.hauteur() * 32;
    }

    public int codeTuile(int i, int j) {
        return this.codesTuiles[i][j];
    }

    public boolean estAccessible(int x, int y) {
        // Vérifier les limites
        if (x < 0 || y < 0) {
            return false;
        }

        int ligne = y / 32;
        int colonne = x / 32;

        // Vérifier les limites du tableau
        if (ligne >= this.codesTuiles.length || colonne >= this.codesTuiles[0].length) {
            return false;
        }

        int codeTuile = this.codesTuiles[ligne][colonne];

        // Debug - garder le style de votre prof
        if (colonne == 9) {
            System.out.println("colonne" + colonne);
        }
        if (ligne == 42) {
            System.out.println("ligne" + ligne);
        }
        if (codeTuile == 2) {
            System.out.println(codeTuile);
        }

        // Retourner true SEULEMENT pour le ciel (1)
        // L'herbe (2) et la terre (3) sont des obstacles solides
        //return codeTuile == 1;
        return switch (codeTuile) {
            case 3,6 -> true;
            default -> false;
        };
    }

